/* css reset */
*{box-sizing: border-box;}
*:before, *:after{box-sizing: border-box;}
*{margin: 0;padding: 0;}
ul,ol{list-style: none;}
a{color: inherit;text-decoration: none;}
img{max-width: 100%;max-height: 100%;}
/* style */
body{background: #eee;}

.globalHeader{
    margin: 20px;
}
@media (min-width: 500px) {
    .globalHeader {
      margin: 60px 0 100px;
    }
  }
.searchForm{
    display: flex;
    justify-content: space-between;
}
@media (min-width: 500px) {
    .searchForm {
      max-width: 400px;
      margin-left: auto;
      margin-right: auto;
    }
  }
.searchForm > input{
    width: 100%;
    margin-right: 10px;
    height: 40px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    box-shadow: 12px 12px 20px rgba(0, 0, 0, 0.2),
    -12px -12px 20px rgba(255, 255, 255, 1);
    cursor: pointer;
    transition: all .2s ease;
}
.searchForm > button{
    white-space: nowrap;
    padding: 0 25px;
    border: none;
    border-radius: 4px;
    background: #0282B9;
    color: white;
    font-size: 16px;
    box-shadow: 12px 12px 20px rgba(0, 0, 0, 0.2),
   -12px -12px 20px rgba(255, 255, 255, 1);
   cursor: pointer;
   transition: all .2s ease;
}

.globalMain{
    max-width: 800px;
    margin-left: auto;
    margin-right: auto;
}

.siteList{
    margin: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}
@media (min-width: 500px){
    .siteList{
      margin-left: 0;
      margin-right: -25px;
      justify-content: flex-start;
    }
  }
.siteList >li{
margin-bottom: 10px;

}
@media(min-width: 500px){
    .siteList > li {
      margin-right: 25px;
    }
  }
.siteList .site{
    width: 160px;
    display:flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: white;
    margin-bottom: 10px;
    border: 1px solid #ddd;
    border-radius: 20px;
    padding: 20px 0;
    position: relative;
    cursor: pointer;
    box-shadow: 12px 12px 20px rgba(0, 0, 0, 0.2),
    -12px -12px 20px rgba(255, 255, 255, 1);
    cursor: pointer;
    transition: all .2s ease;
}
.siteList .site:hover {
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.2),
     0px 0px 0px rgba(255, 255, 255, 1),
    12px 12px 20px rgba(0, 0, 0, 0.2) inset,
     -12px -12px 20px rgba(255, 255, 255, 1) inset;
  }
.siteList .site:active {
     box-shadow: 10px 10px 30px rgba(0, 0, 0, 0.2) inset,
      -10px -10px 30px rgba(255, 255, 255, 1) inset;
  }


.siteList .site > .logo{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}
.siteList .site > .link{
    font-size: 14px;
    margin-top: 4px;
}
.siteList .site > .close{
    position: absolute;
    top:5px;
    right: 10px;
    display: none;
    cursor: default;
}
.siteList .site:hover > .close{
 display: block;
}
.siteList .addButton{
    border: 1px solid #ddd;
    background: white;
    width: 160px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    padding: 20px 0;
    border-radius: 20px;
    box-shadow: 12px 12px 20px rgba(0, 0, 0, 0.2),
   -12px -12px 20px rgba(255, 255, 255, 1);
   cursor: pointer;
   transition: all .2s ease;
}
.siteList .addButton:hover {
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.2),
     0px 0px 0px rgba(255, 255, 255, 1),
    12px 12px 20px rgba(0, 0, 0, 0.2) inset,
     -12px -12px 20px rgba(255, 255, 255, 1) inset;
  }
.siteList .addButton:active {
     box-shadow: 10px 10px 30px rgba(0, 0, 0, 0.2) inset,
      -10px -10px 30px rgba(255, 255, 255, 1) inset;
  }
.siteList .addButton .icon{
    width: 56px;
    height: 56px;

}
.siteList .addButton .text{
    font-size: 14px;
    margin-top: 4px;

}
.siteList .addButton .icon-Wrapper{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}
